import React,{useState} from 'react';
import './App.css';
export default function Comtact(){
    const [form, setFormData]=useState({
        name:'',
        surname:'',
        email:'',
        message:''
    })
    function handleInput(event){
        let newInput={[event.target.name]:event.target.value};
        setFormData({...form,...newInput})
    }
    return(
        <section>
    <div className='form'>
        <h4 style={{marginTop:'50px', marginB}}>Prenons contact!</h4>
      <input 
      className='input'
      type='text'
      name='name'
      value={form.name}
      onChange={handleInput}
      placeholder='ENTREE VOTRE NOM'/>
      <input 
      className='input'
      type='text'
       name='surname'
       value={form.surname}
       onChange={handleInput}
      placeholder='ENTREE VOTRE PRENOM'/>
      <input 
      className='input'
      type='email'
      name='email'
      onChange={handleInput}
      value={form.email}
      placeholder='ADDRESS EMAIL'/>
    </div>
    <h3>ENTREE VOTRE MESSAGE...</h3>
    </section>
    )
}
